import { useState } from "react";
import Classes from "./App.module.css";
import UserInputForm from "./Components/UserInputForm/UserInputForm";
import UserDataList from "./Components/UserDataList/UserDataList";
import Button from "./Components/Reuseable/Button";

const userData = [];

function App() {
	const [updatedData, setUpdatedData] = useState(userData);

	let error = false;

	const formDataHandler = (data) => {
		setUpdatedData((previous) => {
			return [data, ...previous];
		});
	};

	const clickHandler = () => {
		error = false;
	};

	const errorCheckedHandler = ({ name, age }) => {
		if (age < 0 && name.length > 0) {
			error = true;
			return (
				<div
					className={`${Classes.error} ${
						error === false ? Classes.hide : Classes.show
					}`}
				>
					<h3>Invalid input</h3>
					<p>Please enter a valid name and age (non-empty values).</p>
					<Button innerText={"Okay"} bType={"button"} onClick={clickHandler} />
				</div>
			);
		}

		if (age < 0) {
			error = true;
			return (
				<div
					className={`${Classes.error} ${
						error === false ? Classes.hide : Classes.show
					}`}
				>
					<h3>Invalid input</h3>
					<p>Please enter a valid age (> 0).</p>
					<Button innerText={"Okay"} bType={"button"} onClick={clickHandler} />
				</div>
			);
		}
	};

	return (
		<div className={Classes.body}>
			<div className={Classes.container}>
				<UserInputForm
					onFormData={formDataHandler}
					onErrorChecked={errorCheckedHandler}
				/>
				<UserDataList data={updatedData} />
			</div>
		</div>
	);
}

export default App;
