import { useState } from "react";
import Classes from "./App.module.css";
import UserInputForm from "./Components/UserInputForm/UserInputForm";
import UserDataList from "./Components/UserDataList/UserDataList";
import Button from "./Components/Reuseable/Button";

const userData = [];

function App() {
	const [updatedData, setUpdatedData] = useState(userData);
	const [inputsChecked, setInputsChecked] = useState(false);
	const [errorOutput, setErrorOutput] = useState(null);
	const [modalVisibility, setModalVisibility] = useState(true);

	let error = inputsChecked;

	const formDataHandler = (data) => {
		setUpdatedData((previous) => {
			return [data, ...previous];
		});
	};

	const clickHandler = (e) => {
		setModalVisibility(false);
		setInputsChecked(false);
		setErrorOutput(null);
	};

	const errorCheckedHandler = ({ name, age }) => {
		if (age <= 0 && name.length <= 0) {
			error = true;
			setErrorOutput(
				<dialog className={Classes.error} open={modalVisibility}>
					<h3>Invalid input</h3>
					<p>Please enter a valid name and age (non-empty values).</p>
					<Button
						innerText={"Okay"}
						bType={"button"}
						onClickHandler={clickHandler}
					/>
				</dialog>
			);
		} else if (age <= 0) {
			error = true;
			setErrorOutput(
				<dialog
					className={Classes.error} open={modalVisibility}
				>
					<h3>Invalid input</h3>
					<p>Please enter a valid age (age > 0 ).</p>
					<Button
						innerText={"Okay"}
						bType={"button"}
						onClickHandler={clickHandler}
					/>
				</dialog>
			);
		} else if (name.length <= 1) {
			error = true;
			setErrorOutput(
				<dialog
					className={`${Classes.error} ${
						error === false ? Classes.hide : Classes.show
					}`}
				>
					<h3>Invalid input</h3>
					<p>Please enter a valid name (length > 1).</p>
					<Button
						innerText={"Okay"}
						bType={"button"}
						onClickHandler={clickHandler}
					/>
				</dialog>
			);
		}

		if (error) {
			setInputsChecked(true);
		}
		return error;
	};
	return (
		<div className={`${Classes.body}`}>
			<div className={Classes.container}>
				<UserInputForm
					onFormData={formDataHandler}
					onErrorChecked={errorCheckedHandler}
					modal={errorOutput}
				/>
				<UserDataList data={updatedData} />
				{errorOutput};
			</div>
		</div>
	);
}

export default App;
